    auth: {
      bearer: token
    },
    url: "".concat(Cypress.env("PANGEA_BASE_URL"), "/api/v1/Synapse/").concat(sites[i], "/Lights")
  }).then(function (response) {
    response.body.light_n_controller.lights.forEach(function (light) {
      var snappadress_light; //required to identify light
