        failOnStatusCode: false,
        method: 'POST',
        body: {
          lights: [snappadress_light],
          gateway_id: light.controllerId,
          begin_date: Cypress.moment().format('YYYY-MM-DD') + 'T00:00:00-03:00',
          end_date: Cypress.moment(start_date, 'DD-MM-YYYY').add(1, 'days').format('YYYY-MM-DD') + 'T00:00:00-03:00',
          timezone: Intl.DateTimeFormat().resolvedOptions().timeZone
        }
      }).then(function (response) {
