cy.login_n_getLights(_user.usernameCloud, _user.passwordCloud).then(function () {
  var token = window.localStorage.getItem('access_token');
  var sites = window.localStorage.getItem('sites').split(',');
  for (var i = 0; i < sites.length; i++) {
    cy.request({
      auth: {
        bearer: token
      },
      url: "api/v1/Synapse/".concat(sites[i], "/Lights"),
      timeout: 80000,
      retryOnNetworkFailure: true
    }).then(function (response) {
      expect(response.status).to.eq(200);
      expect(response.body).to.have.property('success').eq(true);
      expect(response.body.light_n_controller).to.have.property('controllers');
      var lights = response.body.light_n_controller.lights;
      var lightZones = response.body.light_n_controller.lightZones;
      var controllers = response.body.light_n_controller.controllers;
      var lat, lng;
      lights.forEach(function (light) {
        expect(light).to.have.property('id');
        expect(light).to.have.property('level');
        assert.isNumber(light.level, 'light level is numeric');
        expect(light.level).to.satisfy(function (light_level) {
          return light_level >= 0;
        });
        expect(light).to.have.property('name');
        expect(light).to.have.property('controllerId').to.be.not.null;
        expect(light).to.have.property('geoLocation').to.have.any.keys('latitude', 'longitude');
        lat = light.geoLocation.latitude;
        lng = light.geoLocation.longitude;
        expect(lat).to.match(/^(\-?\d+(\.\d+)?)$/);
        expect(lng).to.match(/^(\-?\d+(\.\d+)?)$/);
      });
      lightZones.forEach(function (lightZone) {
        expect(lightZone).to.have.property('id').not.to.be.null;
        expect(lightZone).to.have.property('zoneId').not.to.be.null;
        expect(lightZone).to.have.property('lightId').not.to.be.null;
      });
      controllers.forEach(function (controller) {
        expect(controller).to.have.property('id').not.to.be.null;
        expect(controller).to.have.property('name').not.to.be.null;
        expect(controller).to.have.property('snapaddr').not.to.be.null;
